        },
        {
            label: "KOTAK PVR INOX CREDIT CARD 5% OFF",
            photo: offer2,
            validity: "sat, march 30, 2025"
        },
    ]
  return (
    <>
            <main className='secondary_container'>
                <section className='container py-3'>
                    <h4 className='text-black'>Offers</h4>
                </section>
            </main>
            <main className='container'>
                <section className='row d-flex'>
                {
                    smapleData && smapleData.length >= 0 ? smapleData.map((item, index) => {
                        return(

                            <div className="card " style={{width: '18rem'}}>
                            <img src={item.photo} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                            </div>
                            </div>

                            // <article key={index} className='col-lg-3 col-md-3 col-sm-12  col-xs-12 my-4 shadow-sm bg-body-tertiary rounded'>
                            //     <img src={item.photo} alt="offers" className='w-100' />
