import "./advertise.css";
import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

// import required modules
import { Pagination } from "swiper/modules";
import argy from "../../utils/images/slider1.avif";
import fail from "../../utils/images/slider2.avif";
import fighter from "../../utils/images/slider3.avif";

const Advertise = () => {
  return (
    <main className="bg_color_slider">
      <div className="mx-5 py-5">
        <Swiper
          slidesPerView={3}
          spaceBetween={30}
          pagination={{
            clickable: true,
          }}
          modules={[Pagination]}
          className="mySwiper"
        >
          <SwiperSlide>
            <img src={argy} alt="sliderAdvertise" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fail} alt="sliderAdvertise" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fighter} alt="sliderAdvertise" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fighter} alt="sliderAdvertise" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={fail} alt="sliderAdvertise" />
          </SwiperSlide>
          <SwiperSlide>
            <img src={argy} alt="sliderAdvertise" />
          </SwiperSlide>
        </Swiper>
      </div>
    </main>
  );
};

export default Advertise;
